= : $selectRow[kolichestvo] - $selectRow[v_schetah]

selectRow: selectRow(table: 'zakaz'; cycle: #h_nomer_zakaza; field: 'kolichestvo'; field: 'v_schetah'; where: 'pozicii_v_zakaze' = #pozicii_v_schete)

format.Val:
